/* eslint-disable react/prop-types */
import React, { useState } from 'react'
import { useSelector } from 'react-redux'
import { selectBlogById } from '../slices/blogsSlice'

const Blog = ({ blogId, incLikes, removeBlog, isRemovable }) => {
    const { title, url, likes, user } = useSelector(state => selectBlogById(state, blogId))
    const [visible, setVisible] = useState(false)

    const removeBtnStyle = {display: isRemovable ? '' : 'none'}

    const toggleVisibility = () => {
        setVisible(!visible)
    }

    return (
        <div className='blogStyle'>
            {title} | {user.name}
            <button onClick={toggleVisibility}>view</button>
            {visible && (
                <div>
                    <div>{url}</div>
                    <div>
                        likes {likes}
                        <button onClick={incLikes}>like</button>
                    </div>
                    <button style={removeBtnStyle} onClick={removeBlog}>remove</button>
                </div>                        
            )}
        </div>
    )
}

export default Blog
